import React from 'react'
import HomePage from './components/PhoneAuth/HomePage';
import { UserProvider } from './components/MyContext';

function App() {
  const user = { name: 'Tania',name: 'Shubh', loggedIn: true }

  return (
    <UserProvider value={user}>
      <HomePage />
    </UserProvider>
  )
}

export default App;
