// --------------------------------------using Class Comp.----------------------------------------------------//
class HomePage extends Component {
  render() {
    return (
      <UserConsumer>
        {props => {
          console.log(props)
          return <div>Name: {props.name} ---- Age: {props.age}</div>
        }}
      </UserConsumer>
    )
  }
}

